default_pic_src = "data:image/png;base64,"


class Student:
    def __init__(self, id, name, info, pic=default_pic_src):
        self.id = id
        self.name = name
        self.info = info
        self.pic = pic

    def to_json(self):
        return {
            "id": self.id,
            "name": self.name,
            "info": self.info,
            "pic": self.pic
        }

    def __repr__(self):
        return str(self.to_json())


student_1 = Student(0, "Student 1", "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Facilis, ut.")
student_2 = Student(1, "Student 2", "Lorem ipsum dolor sit amet consectetur, adipisicing elit. Beatae dolore repudiandae, aliquid enim deserunt facilis id? Aut quisquam saepe velit.")
student_3 = Student(2, "Student 3", "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Possimus harum fugit beatae ea repellendus hic asperiores molestiae facilis recusandae, sint expedita modi voluptatibus dolorem odit reiciendis est cum inventore voluptatum.")
student_4 = Student(3, "Student 4", "Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolores ipsa tempore enim eaque inventore nemo aspernatur voluptatem fugiat autem facere placeat obcaecati quam, consectetur consequuntur aliquam! Perferendis quibusdam facere porro aliquid, sapiente tempore quae a obcaecati nesciunt aliquam! Maiores, iure.")


students = [student_1, student_2, student_3, student_4]


def init_students():
    return students
